// src/App.js
import React, { useState } from "react";
import "./App.css"; // Import your global CSS
import ProductList from "./components/ProductList";
import Cart from "./components/Cart";
import Checkout from "./components/Checkout";

const products = [
  // Sample product data
  {
    id: 1,
    name: "Product 1",
    category: "Category A",
    price: 25,
    rating: 4,
    image: "product1.jpg",
  },
  // Add more products
];

function App() {

  const [cartItems, setCartItems] = useState([]);
  const [checkoutComplete, setCheckoutComplete] = useState(false);

  const addToCart = (product) => {
    const updatedCart = [...cartItems, { ...product, quantity: 1 }];
    setCartItems(updatedCart);
  };

  const handleCheckoutComplete = () => {
    setCartItems([]); // Clear the cart after checkout
    setCheckoutComplete(true);
  };

  return (
    <div className="App">
      <h1>Product Listing</h1>
      <ProductList products={products} addToCart={addToCart} />
      <Cart cartItems={cartItems} updateCart={setCartItems} />
      {cartItems.length > 0 && !checkoutComplete && (
        <Checkout
          cartItems={cartItems}
          cartTotal={cartTotal}
          checkoutComplete={handleCheckoutComplete}
        />
      )}
      {checkoutComplete && <p>Thank you for your purchase!</p>}
    </div>
  );
}

export default App;
